import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{o as a,c as l,f as e}from"./app-710e88e4.js";const r={},h=e('<h3 id="软件测试以及目的" tabindex="-1"><a class="header-anchor" href="#软件测试以及目的" aria-hidden="true">#</a> 软件测试以及目的？</h3><p>使用技术手段验证软件是否满足使用需求。减少软件 BUG 保障软件质量。</p><h3 id="测试主流技能" tabindex="-1"><a class="header-anchor" href="#测试主流技能" aria-hidden="true">#</a> 测试主流技能</h3><ul><li><p>功能测试：通过测试文档中的用例，验证请求是否达到预期结果。</p></li><li><p>自动化测试：使用代码或者工具代替手工测试。</p></li><li><p>接口测试：使用代码或者工具对服务端提供的接口进行测试。</p></li><li><p>性能测试：模拟多人使用软件，查找服务器缺陷。</p></li></ul><h3 id="常见测试分类" tabindex="-1"><a class="header-anchor" href="#常见测试分类" aria-hidden="true">#</a> 常见测试分类</h3><h4 id="按测试阶段进行划分" tabindex="-1"><a class="header-anchor" href="#按测试阶段进行划分" aria-hidden="true">#</a> 按测试阶段进行划分</h4><ul><li>单元测试：针对程序源代码进行测试；</li><li>集成测试：又称接口测试，针对模块之间访问地址进行测试；</li><li>系统测试：对整个系统进行测试，包括功能，兼容，文档测试等；</li><li>验收测试：主要分为内测，公测，使用不同的人群发掘项目缺陷。</li></ul><h4 id="按代码可见度划分" tabindex="-1"><a class="header-anchor" href="#按代码可见度划分" aria-hidden="true">#</a> 按代码可见度划分</h4><ul><li>黑盒测试：源代码不可见，UI 功能可见。</li><li>灰盒测试：部分源代码可见，功能不可见。（集成测试，接口测试）</li><li>白盒测试：全部代码可见，功能不可见。</li></ul><h3 id="质量模型" tabindex="-1"><a class="header-anchor" href="#质量模型" aria-hidden="true">#</a> 质量模型</h3><p>衡量一个优秀软件的维度。</p><ul><li>功能性，功能的正确实现，错误处理情况。</li><li>性能，服务器压力测试，服务器的硬件配置是否满足。</li><li>兼容性，测试在不同的操作系统，浏览器，手机上能否正确运行软件。</li><li>易用性，软件是否满足简洁，友好，流畅，美观。</li><li>可靠性，软件不会出现闪退，卡死等情况。</li><li>安全，隐私数据能否加密传输。</li><li>可维护性，是否易于维护。</li><li>可移植性，后续能否移植到新的平台上。</li></ul>',12),d=[h];function t(c,n){return a(),l("div",null,d)}const u=i(r,[["render",t],["__file","1testbasic.html.vue"]]);export{u as default};
